INSERT INTO lehrstellen (id, company_id, beruf_code, title, description, requirements, education_type, start_date, duration_years, canton, city, postal_code, status, culture_description) VALUES
('b1000000-0000-0000-0000-000000000011'::uuid, 'a1000000-0000-0000-0000-000000000009'::uuid, '45700', 'Polymechaniker/in EFZ', 'Bei ABB lernst du in einer hochmodernen Lehrwerkstatt die Herstellung von Präzisionsbauteilen für elektrische Antriebe und Robotersysteme. Du arbeitest mit CNC-Maschinen und wirst Teil eines internationalen Technologiekonzerns. Nach der Lehre stehen dir spannende Karrieremöglichkeiten in der Automation und Elektrotechnik offen.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Mathematik und Physik", "Technisches Verständnis und handwerkliches Geschick", "Präzise und sorgfältige Arbeitsweise"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8050', 'active', 'Innovation und Präzision stehen bei uns im Mittelpunkt. Du arbeitest in einem internationalen Umfeld mit modernster Technologie und profitierst von erstklassiger Ausbildung.'),
('b1000000-0000-0000-0000-000000000012'::uuid, 'a1000000-0000-0000-0000-000000000009'::uuid, '47600', 'Automatiker/in EFZ', 'Als Automatiker/in bei ABB entwickelst und programmierst du Steuerungen für komplexe Automatisierungssysteme. Du lernst den Umgang mit SPS-Programmierung, Robotik und Netzwerktechnik. Deine Ausbildung findet in enger Zusammenarbeit mit unseren Entwicklungsteams statt.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik und Informatik", "Logisches Denkvermögen und technisches Interesse", "Teamfähigkeit und Kommunikationsstärke"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8050', 'active', 'Bei uns arbeitest du an Zukunftstechnologien und lernst von erfahrenen Fachleuten. Wir fördern deine Entwicklung durch interne Schulungen und internationale Projekte.'),
('b1000000-0000-0000-0000-000000000013'::uuid, 'a1000000-0000-0000-0000-000000000009'::uuid, '46500', 'Elektroinstallateur/in EFZ', 'In der ABB Elektroinstallationslehre lernst du die Installation und Inbetriebnahme von elektrischen Anlagen und Verteilsystemen. Du arbeitest an Projekten in unseren Produktionshallen und Bürogebäuden. Die Ausbildung verbindet klassisches Handwerk mit modernster Gebäudetechnik.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Gute Leistungen in Mathematik und Physik", "Handwerkliches Geschick und räumliches Vorstellungsvermögen", "Zuverlässigkeit und Verantwortungsbewusstsein"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8050', 'active', 'Du profitierst von der Expertise eines weltweit führenden Elektrotechnikkonzerns und erhältst Einblick in innovative Installationstechnologien und Smart-Building-Lösungen.'),
('b1000000-0000-0000-0000-000000000014'::uuid, 'a1000000-0000-0000-0000-000000000009'::uuid, '88611', 'Informatiker/in EFZ Applikationsentwicklung', 'Als Informatiklernende/r bei ABB entwickelst du Software für industrielle Steuerungssysteme und interne Business-Applikationen. Du arbeitest mit modernen Programmiersprachen und agilen Methoden. Deine Projekte haben direkten Einfluss auf die Digitalisierung unserer Produktionsprozesse.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik und Englisch", "Logisches Denkvermögen und Interesse an Programmierung", "Selbstständige und strukturierte Arbeitsweise"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8050', 'active', 'Du tauchst ein in die Welt der Industrie 4.0 und arbeitest an spannenden Digitalisierungsprojekten. Unser IT-Team unterstützt dich mit Mentoring und modernen Entwicklungstools.'),
('b1000000-0000-0000-0000-000000000015'::uuid, 'a1000000-0000-0000-0000-000000000009'::uuid, '68500', 'Kaufmann/-frau EFZ', 'In der kaufmännischen Lehre bei ABB durchläufst du verschiedene Abteilungen wie Einkauf, Marketing und HR. Du lernst internationale Geschäftsprozesse kennen und arbeitest in einem multikulturellen Umfeld. Die Ausbildung bereitet dich auf eine Karriere in einem globalen Technologiekonzern vor.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Deutsch, Englisch und Mathematik", "Organisationstalent und Kommunikationsstärke", "Interesse an wirtschaftlichen Zusammenhängen"]', 'EFZ', '2027-08-01', 3, 'ZH', 'Zürich', '8050', 'active', 'Bei ABB erlebst du internationale Geschäftswelt hautnah. Du arbeitest in professionellen Teams und erhältst umfassende Einblicke in die Prozesse eines Weltkonzerns.'),
('b1000000-0000-0000-0000-000000000016'::uuid, 'a1000000-0000-0000-0000-000000000010'::uuid, '86914', 'Fachmann/-frau Gesundheit EFZ', 'Bei Novartis arbeitest du in unserem Betriebsambulatorium und unterstützt unser medizinisches Team bei der Betreuung unserer Mitarbeitenden. Du lernst Grundpflege, medizinische Unterstützung und administrative Tätigkeiten im Gesundheitswesen. Die Ausbildung kombiniert medizinisches Fachwissen mit der Dynamik eines Pharmaunternehmens.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Gute Leistungen in Biologie und Deutsch", "Einfühlungsvermögen und soziale Kompetenz", "Belastbarkeit und Diskretion"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4056', 'active', 'Du arbeitest in einem professionellen medizinischen Umfeld eines führenden Pharmaunternehmens. Wir bieten dir moderne Infrastruktur und die Möglichkeit, Gesundheitsversorgung auf höchstem Niveau kennenzulernen.'),
('b1000000-0000-0000-0000-000000000017'::uuid, 'a1000000-0000-0000-0000-000000000010'::uuid, '88611', 'Informatiker/in EFZ Applikationsentwicklung', 'Als Informatiklernende/r bei Novartis entwickelst du Software für Forschungs- und Produktionsprozesse in der Pharmaindustrie. Du arbeitest mit Cloud-Technologien, Datenbanken und modernen Frameworks. Deine Anwendungen unterstützen lebensrettende Medikamentenentwicklung.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik, Informatik und Englisch", "Analytisches Denkvermögen und Problemlösungskompetenz", "Interesse an Life Sciences und Technologie"]', 'EFZ', '2027-08-01', 4, 'BS', 'Basel', '4056', 'active', 'Du bist Teil der digitalen Transformation in der Pharmaindustrie. Bei uns arbeitest du an hochmodernen Projekten, die echten Impact auf die Gesundheit von Menschen weltweit haben.'),
('b1000000-0000-0000-0000-000000000018'::uuid, 'a1000000-0000-0000-0000-000000000010'::uuid, '34500', 'Zeichner/in EFZ Architektur', 'In der Novartis Campus Development Abteilung planst du Laborgebäude und Forschungseinrichtungen mit CAD-Software. Du arbeitest an anspruchsvollen Bauprojekten mit speziellen Anforderungen an Reinräume und Laborsicherheit. Die Ausbildung verbindet Architektur mit pharmazeutischer Infrastruktur.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Mathematik und Geometrie", "Räumliches Vorstellungsvermögen und Zeichentalent", "Präzision und Detailgenauigkeit"]', 'EFZ', '2027-08-01', 4, 'BS', 'Basel', '4056', 'active', 'Du arbeitest an der Planung zukunftsweisender Forschungsgebäude auf unserem Campus. Erlebe die Verbindung von Architektur, Technik und pharmazeutischer Innovation.'),
('b1000000-0000-0000-0000-000000000019'::uuid, 'a1000000-0000-0000-0000-000000000010'::uuid, '68500', 'Kaufmann/-frau EFZ', 'In der kaufmännischen Lehre bei Novartis arbeitest du in verschiedenen Bereichen wie Procurement, Finance oder Human Resources. Du erhältst Einblick in globale Geschäftsprozesse eines führenden Pharmaunternehmens. Die internationale Ausrichtung und wissenschaftliche Umgebung machen diese Lehre besonders spannend.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Deutsch, Englisch und Mathematik", "Organisationsfähigkeit und selbstständiges Arbeiten", "Interesse an Wissenschaft und Wirtschaft"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4056', 'active', 'Werde Teil eines globalen Healthcare-Leaders. Du arbeitest in einem inspirierenden Umfeld, wo Innovation und wissenschaftliche Exzellenz gelebt werden.'),
('b1000000-0000-0000-0000-000000000020'::uuid, 'a1000000-0000-0000-0000-000000000010'::uuid, '64800', 'Interactive Media Designer EFZ', 'Als Interactive Media Designer bei Novartis gestaltest du digitale Kommunikationsmittel für interne und externe Zielgruppen. Du erstellst Videos, Animationen und interaktive Präsentationen für medizinische Fachkongresse. Deine Kreativität trifft auf wissenschaftliche Präzision.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Deutsch und Englisch", "Kreativität und gestalterisches Flair", "Interesse an digitalen Medien und Design-Software"]', 'EFZ', '2027-08-01', 4, 'BS', 'Basel', '4056', 'active', 'Du gestaltest Kommunikation für ein globales Publikum und lernst, komplexe wissenschaftliche Inhalte visuell ansprechend aufzubereiten. Modernste Tools und kreative Freiheit erwarten dich.'),
('b1000000-0000-0000-0000-000000000021'::uuid, 'a1000000-0000-0000-0000-000000000011'::uuid, '95600', 'Logistiker/in EFZ', 'Bei der Post lernst du die Organisation und Steuerung komplexer Logistikprozesse in Brief- und Paketzentren. Du arbeitest mit modernen Fördersystemen und Scanner-Technologie. Die Ausbildung bietet dir vielfältige Einsatzmöglichkeiten in einem der grössten Logistikunternehmen der Schweiz.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Gute Leistungen in Mathematik und Deutsch", "Organisationstalent und räumliches Denkvermögen", "Körperliche Belastbarkeit und Teamfähigkeit"]', 'EFZ', '2027-08-01', 3, 'BE', 'Bern', '3030', 'active', 'Du bist Teil der gelben Familie und sorgst dafür, dass täglich Millionen Sendungen pünktlich ankommen. Modernste Logistiktechnologie und ein starkes Team erwarten dich.'),
('b1000000-0000-0000-0000-000000000022'::uuid, 'a1000000-0000-0000-0000-000000000011'::uuid, '88611', 'Informatiker/in EFZ Applikationsentwicklung', 'Als Informatiklernende/r bei der Post entwickelst du digitale Lösungen für Logistik, E-Commerce und Finanzdienstleistungen. Du arbeitest an Apps, Webportalen und Backend-Systemen für Millionen von Kunden. Deine Software prägt die digitale Zukunft der Post.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik und Englisch", "Logisches Denkvermögen und Programmiererfahrung von Vorteil", "Teamfähigkeit und Kundenorientierung"]', 'EFZ', '2027-08-01', 4, 'BE', 'Bern', '3030', 'active', 'Du entwickelst Software, die das Leben von Millionen Menschen erleichtert. Bei uns arbeitest du mit modernen Technologien an der digitalen Transformation eines Schweizer Traditionsunternehmens.'),
('b1000000-0000-0000-0000-000000000023'::uuid, 'a1000000-0000-0000-0000-000000000011'::uuid, '47600', 'Automatiker/in EFZ', 'In der Automatikerlehre bei der Post wartest und programmierst du Sortier- und Förderanlagen in unseren Briefzentren. Du lernst Robotik, SPS-Programmierung und Netzwerktechnik. Die hochautomatisierten Anlagen gehören zu den modernsten in Europa.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Mathematik, Physik und Informatik", "Technisches Verständnis und logisches Denken", "Verantwortungsbewusstsein und Präzision"]', 'EFZ', '2027-08-01', 4, 'BE', 'Bern', '3030', 'active', 'Du arbeitest an hochmodernen Automatisierungssystemen, die täglich Millionen Briefe und Pakete bewegen. Spannende Technik und abwechslungsreiche Einsätze erwarten dich.'),
('b1000000-0000-0000-0000-000000000024'::uuid, 'a1000000-0000-0000-0000-000000000011'::uuid, '68500', 'Kaufmann/-frau EFZ', 'Als kaufmännische/r Lernende/r bei der Post durchläufst du verschiedene Bereiche wie Kundenservice, Marketing und Finanzen. Du lernst die vielfältigen Dienstleistungen der Post kennen - von Briefpost über Pakete bis zu Finanzservices. Die Ausbildung ist abwechslungsreich und kundenorientiert.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Deutsch, Französisch und Mathematik", "Kommunikationsstärke und Kundenorientierung", "Organisationstalent und Flexibilität"]', 'EFZ', '2027-08-01', 3, 'BE', 'Bern', '3030', 'active', 'Bei der Post arbeitest du für ein Unternehmen, das zur Schweiz gehört wie die Berge. Du bist nah bei den Kunden und lernst vielfältige Geschäftsbereiche kennen.'),
('b1000000-0000-0000-0000-000000000025'::uuid, 'a1000000-0000-0000-0000-000000000011'::uuid, '95700', 'Logistiker/in EBA', 'In der zweijährigen Logistikausbildung bei der Post lernst du die Grundlagen der Paket- und Briefverarbeitung. Du arbeitest mit Scannern, Sortiermaschinen und im Lager. Nach erfolgreichem Abschluss kannst du die verkürzte EFZ-Lehre anhängen.', '["Abgeschlossene Sekundarschule Niveau B oder C", "Grundlegende Deutschkenntnisse und Rechenfähigkeit", "Zuverlässigkeit und Pünktlichkeit", "Teamfähigkeit und körperliche Belastbarkeit"]', 'EBA', '2027-08-01', 2, 'BE', 'Bern', '3030', 'active', 'Bei der Post erhältst du eine solide Grundausbildung in der Logistik. Wir unterstützen dich individuell und bieten dir die Chance, dich weiterzuentwickeln.'),
('b1000000-0000-0000-0000-000000000026'::uuid, 'a1000000-0000-0000-0000-000000000012'::uuid, '71300', 'Detailhandelsfachmann/-frau EFZ', 'Bei Coop lernst du alle Facetten des modernen Detailhandels kennen - von der Warenbestellung über Kundenberatung bis zur Verkaufsförderung. Du arbeitest in verschiedenen Abteilungen wie Frische, Non-Food oder an der Kasse. Die Ausbildung ist vielseitig und kundenorientiert.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Gute Leistungen in Deutsch und Mathematik", "Freude am Umgang mit Menschen", "Flexibilität und Belastbarkeit"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4002', 'active', 'Bei Coop bist du Teil der grössten Detailhandelsgenossenschaft der Schweiz. Du arbeitest in einem dynamischen Team und profitierst von vielfältigen Weiterbildungsmöglichkeiten.'),
('b1000000-0000-0000-0000-000000000027'::uuid, 'a1000000-0000-0000-0000-000000000012'::uuid, '79000', 'Koch/Köchin EFZ', 'In der Coop Restaurant Lehre lernst du die Zubereitung von frischen, saisonalen Gerichten für unsere Gäste. Du arbeitest in modernen Küchen und lernst von erfahrenen Köchen. Die Ausbildung verbindet klassische Kochkunst mit Grossküchentechnik.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Interesse an Lebensmitteln und Kochen", "Kreativität und handwerkliches Geschick", "Teamfähigkeit und Belastbarkeit auch zu Stosszeiten"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4002', 'active', 'Du kochst mit frischen Produkten in einem modernen Umfeld. Bei Coop lernst du effiziente Arbeitsabläufe und kannst deine kulinarische Kreativität entfalten.'),
('b1000000-0000-0000-0000-000000000028'::uuid, 'a1000000-0000-0000-0000-000000000012'::uuid, '21104', 'Bäcker/in-Konditor/in EFZ', 'In unseren Coop Backstuben stellst du täglich frisches Brot, Brötchen und feine Backwaren her. Du lernst traditionelles Handwerk und moderne Produktionstechniken. Die Ausbildung umfasst Bäckerei und Konditorei.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Interesse an Lebensmittelhandwerk", "Kreativität und Fingerfertigkeit", "Bereitschaft zu frühen Arbeitszeiten und körperlicher Arbeit"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4002', 'active', 'Bei Coop lernst du das traditionelle Bäckerhandwerk mit modernster Technik. Du produzierst qualitativ hochwertige Backwaren für tausende zufriedene Kunden.'),
('b1000000-0000-0000-0000-000000000029'::uuid, 'a1000000-0000-0000-0000-000000000012'::uuid, '95600', 'Logistiker/in EFZ', 'In der Logistiklehre bei Coop arbeitest du in einem unserer hochmodernen Verteilzentren. Du lernst Wareneingang, Kommissionierung, Lagerverwaltung und Distribution. Mit modernsten Systemen sorgst du dafür, dass täglich tausende Artikel in die Filialen gelangen.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Gute Leistungen in Mathematik", "Organisationstalent und räumliches Denkvermögen", "Körperliche Fitness und Teamgeist"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4002', 'active', 'Du bist das Rückgrat unserer Warenversorgung. Moderne Technologie, effiziente Prozesse und ein starkes Team machen deine Ausbildung spannend und abwechslungsreich.'),
('b1000000-0000-0000-0000-000000000030'::uuid, 'a1000000-0000-0000-0000-000000000012'::uuid, '68500', 'Kaufmann/-frau EFZ', 'Als kaufmännische/r Lernende/r in der Coop Zentrale arbeitest du in Bereichen wie Einkauf, Marketing, HR oder Finanzen. Du lernst die Prozesse eines der grössten Schweizer Detailhändler kennen. Die Ausbildung ist vielseitig und bietet Einblick in verschiedene Geschäftsfelder.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Deutsch, Französisch und Mathematik", "Kommunikationsstärke und Organisationstalent", "Interesse am Detailhandel und Kundenorientierung"]', 'EFZ', '2027-08-01', 3, 'BS', 'Basel', '4002', 'active', 'Bei Coop erlebst du die Vielfalt des Detailhandels aus der Zentrale. Du arbeitest an Projekten, die direkten Einfluss auf unsere Filialen und Millionen Kunden haben.'),
('b1000000-0000-0000-0000-000000000031'::uuid, 'a1000000-0000-0000-0000-000000000013'::uuid, '68500', 'Kaufmann/-frau EFZ', 'Bei Raiffeisen lernst du das Bankgeschäft von Grund auf - von der Kundenberatung über Kredite bis zu Anlageprodukten. Du arbeitest in einer Raiffeisen-Bank in deiner Region und hast direkten Kundenkontakt. Die genossenschaftlichen Werte prägen deine Ausbildung.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Deutsch, Mathematik und Englisch", "Kommunikationsstärke und gepflegtes Auftreten", "Interesse an Wirtschaft und Finanzthemen"]', 'EFZ', '2027-08-01', 3, 'SG', 'St. Gallen', '9001', 'active', 'Bei Raiffeisen arbeitest du nah bei den Kunden in deiner Region. Wir leben genossenschaftliche Werte und fördern deine persönliche und fachliche Entwicklung.'),
('b1000000-0000-0000-0000-000000000032'::uuid, 'a1000000-0000-0000-0000-000000000013'::uuid, '88611', 'Informatiker/in EFZ Applikationsentwicklung', 'Als Informatiklernende/r bei Raiffeisen entwickelst du Banking-Software und digitale Kundenservices. Du arbeitest an E-Banking-Lösungen, Mobile Apps und internen Systemen. Die Ausbildung verbindet IT mit Finanzwissen.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik und Englisch", "Logisches Denkvermögen und Interesse an Programmierung", "Zuverlässigkeit und Diskretion"]', 'EFZ', '2027-08-01', 4, 'SG', 'St. Gallen', '9001', 'active', 'Du entwickelst digitale Banking-Lösungen für über 3.7 Millionen Kunden. Bei uns arbeitest du mit modernen Technologien im sicheren Umfeld des Finanzwesens.'),
('b1000000-0000-0000-0000-000000000033'::uuid, 'a1000000-0000-0000-0000-000000000013'::uuid, '88900', 'ICT-Fachmann/-frau EFZ', 'In der ICT-Lehre bei Raiffeisen betreust du IT-Arbeitsplätze, Netzwerke und unterstützt unsere Mitarbeitenden bei technischen Fragen. Du lernst Server-Administration, Client-Management und IT-Support. Die Ausbildung ist vielseitig und praxisnah.', '["Abgeschlossene Sekundarschule Niveau A oder B", "Gute Leistungen in Mathematik und Englisch", "Technisches Verständnis und Serviceorientierung", "Kommunikationsfähigkeit und Problemlösungskompetenz"]', 'EFZ', '2027-08-01', 3, 'SG', 'St. Gallen', '9001', 'active', 'Du sorgst dafür, dass die IT-Infrastruktur von über 200 Raiffeisen-Banken reibungslos funktioniert. Vielseitige Aufgaben und moderne Technologien erwarten dich.'),
('b1000000-0000-0000-0000-000000000034'::uuid, 'a1000000-0000-0000-0000-000000000014'::uuid, '68500', 'Kaufmann/-frau EFZ', 'Bei UBS durchläufst du eine erstklassige kaufmännische Ausbildung in einer der führenden Banken weltweit. Du arbeitest in verschiedenen Bereichen wie Kundenberatung, Trading oder Vermögensverwaltung. Die internationale Ausrichtung und das professionelle Umfeld machen diese Lehre besonders wertvoll.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Deutsch, Englisch und Mathematik", "Gepflegtes Auftreten und hohe Sozialkompetenz", "Interesse an Finanzwelt und wirtschaftlichen Zusammenhängen"]', 'EFZ', '2027-08-01', 3, 'ZH', 'Zürich', '8001', 'active', 'Du startest deine Karriere bei einer globalen Bank mit höchsten Standards. Professionelles Umfeld, internationale Perspektiven und exzellente Entwicklungsmöglichkeiten erwarten dich.'),
('b1000000-0000-0000-0000-000000000035'::uuid, 'a1000000-0000-0000-0000-000000000014'::uuid, '88611', 'Informatiker/in EFZ Applikationsentwicklung', 'Als Informatiklernende/r bei UBS entwickelst du Software für Banking-Systeme, Trading-Plattformen und Wealth-Management-Tools. Du arbeitest mit neuesten Technologien in einem hochsicheren Umfeld. Deine Programme bewegen täglich Milliarden an Vermögenswerten.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik, Informatik und Englisch", "Analytisches Denkvermögen und Programmierkenntnisse", "Hohe Auffassungsgabe und Diskretion"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8001', 'active', 'Du entwickelst Software für eine der weltweit führenden Banken. Höchste technische Standards, Sicherheit und Innovation prägen deine Ausbildung bei uns.'),
('b1000000-0000-0000-0000-000000000036'::uuid, 'a1000000-0000-0000-0000-000000000014'::uuid, '88612', 'Informatiker/in EFZ Plattformentwicklung', 'In der Plattformentwicklung bei UBS betreust du Server, Datenbanken und Cloud-Infrastruktur für globale Banking-Systeme. Du lernst Systemadministration, Virtualisierung und Netzwerksicherheit. Die Ausbildung bietet dir tiefe Einblicke in Enterprise-IT.', '["Abgeschlossene Sekundarschule Niveau A", "Sehr gute Leistungen in Mathematik und Englisch", "Technisches Verständnis und logisches Denkvermögen", "Zuverlässigkeit und strukturierte Arbeitsweise"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8001', 'active', 'Du baust und betreibst die IT-Infrastruktur für eine globale Bank. Sicherheit, Verfügbarkeit und Performance haben bei uns höchste Priorität.'),
('b1000000-0000-0000-0000-000000000037'::uuid, 'a1000000-0000-0000-0000-000000000014'::uuid, '88613', 'Mediamatiker/in EFZ', 'Als Mediamatiker/in bei UBS gestaltest du digitale und Print-Medien für interne und externe Kommunikation. Du erstellst Präsentationen, Videos und interaktive Inhalte für Kundenevents und Mitarbeiterkommunikation. Die Ausbildung verbindet Kreativität mit technischem Know-how.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Deutsch, Englisch und Informatik", "Kreativität und gestalterisches Talent", "Kommunikationsstärke und Organisationsfähigkeit"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8001', 'active', 'Du gestaltest Kommunikation für eine internationale Marke. Professionelle Tools, anspruchsvolle Projekte und ein kreatives Umfeld erwarten dich.'),
('b1000000-0000-0000-0000-000000000038'::uuid, 'a1000000-0000-0000-0000-000000000014'::uuid, '64800', 'Interactive Media Designer EFZ', 'In der Interactive Media Design Lehre bei UBS entwickelst du digitale Erlebnisse für Kunden und Mitarbeitende. Du gestaltest Websites, Apps und interaktive Installationen für Bankfilialen. Die Ausbildung kombiniert UX-Design, Entwicklung und Branding.', '["Abgeschlossene Sekundarschule Niveau A", "Gute Leistungen in Deutsch, Englisch und Gestalten", "Kreativität und Affinität zu digitalen Medien", "Teamfähigkeit und Kundenorientierung"]', 'EFZ', '2027-08-01', 4, 'ZH', 'Zürich', '8001', 'active', 'Du gestaltest die digitale Customer Experience einer globalen Bank. Modernste Design-Tools, agile Methoden und internationale Projekte erwarten dich.'),
